  public static function system_produk($page)
  {
    // $data_detail = Ecommerce::list_barang_detail($page, $id = "");
    DB::table('store__produk');
    DB::whereRaw("id=" . $page['load']['id']);
    $produk = DB::get('all');
    if (!$produk['row'][0]->id_apps_store_produk) {
      $produk['row'][0]->id_apps_store_produk = EcommerceApp::diferent_value_id_apps($produk['row'][0]->id);
    }
    $return['css'] = '';
    $return["js"] = IndexedDBContent::barang($page) . '
    <script>
      setItemBarang("' . $produk['row'][0]->id_apps_store_produk . '");
      var data_produk = getItemBarang("' . $produk['row'][0]->id_apps_store_produk . '");
      if(data_produk){
        parsehtmldata_produk("' . $produk['row'][0]->id_apps_store_produk . '");
      }
    </script>

    ';
    $return["html"] = '<PRODUK-DETAIL></PRODUK-DETAIL>
    <div id="content-data_produk"></div>
    ';
    return $return;
  }
